import React, { useState, FormEvent } from 'react';
import { personalInfo, translations } from '../data/mock';
import {
  Mail,
  Phone,
  MapPin,
  Send,
  MessageSquare
} from 'lucide-react';

interface ContactProps {
  lang: 'en' | 'id';
}

interface FormData {
  name: string;
  email: string;
  subject: string;
  message: string;
}

const Contact: React.FC<ContactProps> = ({ lang }) => {
  const [formData, setFormData] = useState<FormData>({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [errors, setErrors] = useState<Partial<FormData>>({});

  const t = translations[lang];

  const validateForm = (): boolean => {
    const newErrors: Partial<FormData> = {};

    if (!formData.name.trim()) newErrors.name = 'Required';
    if (!formData.email.trim()) {
      newErrors.email = 'Required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Invalid email';
    }
    if (!formData.subject.trim()) newErrors.subject = 'Required';
    if (!formData.message.trim()) newErrors.message = 'Required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e: FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsSubmitting(true);

    // Simulasi submit (siap dihubungkan ke backend)
    setTimeout(() => {
      console.log('Form submitted:', formData);
      setIsSubmitting(false);
      setShowSuccess(true);
      setFormData({
        name: '',
        email: '',
        subject: '',
        message: ''
      });

      setTimeout(() => setShowSuccess(false), 5000);
    }, 1500);
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name as keyof FormData]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }
  };

  const openWhatsApp = () => {
    const message = encodeURIComponent(
      lang === 'en'
        ? 'Hello! I would like to discuss a project with you.'
        : 'Halo! Saya ingin mendiskusikan proyek dengan Anda.'
    );

    window.open(
      `https://wa.me/${personalInfo.whatsapp}?text=${message}`,
      '_blank'
    );
  };

  return (
    <section id="contact" className="px-6 py-20 bg-gray-800">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="mb-16 text-center">
          <h2 className="mb-4 text-4xl font-bold text-white md:text-5xl">
            {t.contact.title}
          </h2>
          <p className="text-lg text-gray-400">
            {t.contact.subtitle}
          </p>
        </div>

        <div className="grid grid-cols-1 gap-8 lg:grid-cols-3">
          {/* CONTACT INFO */}
          <div className="space-y-6">
            {/* Email */}
            <div className="p-6 bg-gray-900 border border-gray-700 rounded-2xl hover:border-green-500/50">
              <div className="flex items-center justify-center w-12 h-12 mb-4 bg-gradient-to-br from-green-500/20 to-blue-500/20 rounded-xl">
                <Mail className="w-6 h-6 text-green-400" />
              </div>
              <h3 className="mb-2 font-semibold text-white">
                {t.contact.info.email}
              </h3>
              <a
                href={`mailto:${personalInfo.email}`}
                className="text-sm text-gray-400 break-all hover:text-green-400"
              >
                {personalInfo.email}
              </a>
            </div>

            {/* Phone */}
            <div className="p-6 bg-gray-900 border border-gray-700 rounded-2xl hover:border-green-500/50">
              <div className="flex items-center justify-center w-12 h-12 mb-4 bg-gradient-to-br from-green-500/20 to-blue-500/20 rounded-xl">
                <Phone className="w-6 h-6 text-green-400" />
              </div>
              <h3 className="mb-2 font-semibold text-white">
                {t.contact.info.phone}
              </h3>
              <p className="text-sm text-gray-400">
                {personalInfo.phone}
              </p>
            </div>

            {/* Location */}
            <div className="p-6 bg-gray-900 border border-gray-700 rounded-2xl hover:border-green-500/50">
              <div className="flex items-center justify-center w-12 h-12 mb-4 bg-gradient-to-br from-green-500/20 to-blue-500/20 rounded-xl">
                <MapPin className="w-6 h-6 text-green-400" />
              </div>
              <h3 className="mb-2 font-semibold text-white">
                {t.contact.info.location}
              </h3>
              <p className="text-sm text-gray-400">
                {personalInfo.location[lang]}
              </p>
            </div>

            {/* WhatsApp */}
            <button
              onClick={openWhatsApp}
              className="flex items-center justify-center w-full gap-2 px-6 py-4 font-medium text-white bg-green-500 rounded-xl hover:bg-green-600"
            >
              <MessageSquare className="w-5 h-5" />
              WhatsApp
            </button>
          </div>

          {/* CONTACT FORM */}
          <div className="p-8 bg-gray-900 border border-gray-700 rounded-2xl lg:col-span-2">
            <h3 className="mb-6 text-2xl font-semibold text-white">
              {t.contact.form.title}
            </h3>

            {showSuccess && (
              <div className="p-4 mb-6 text-sm text-green-400 border bg-green-500/10 border-green-500/30 rounded-xl">
                {t.contact.form.success}
              </div>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
              <input
                type="text"
                name="name"
                placeholder={t.contact.form.name}
                value={formData.name}
                onChange={handleChange}
                className="w-full px-4 py-3 text-white bg-gray-800 border border-gray-700 rounded-xl"
              />

              <input
                type="email"
                name="email"
                placeholder={t.contact.form.email}
                value={formData.email}
                onChange={handleChange}
                className="w-full px-4 py-3 text-white bg-gray-800 border border-gray-700 rounded-xl"
              />

              <input
                type="text"
                name="subject"
                placeholder={t.contact.form.subject}
                value={formData.subject}
                onChange={handleChange}
                className="w-full px-4 py-3 text-white bg-gray-800 border border-gray-700 rounded-xl"
              />

              <textarea
                name="message"
                rows={5}
                placeholder={t.contact.form.message}
                value={formData.message}
                onChange={handleChange}
                className="w-full px-4 py-3 text-white bg-gray-800 border border-gray-700 resize-none rounded-xl"
              />

              <button
                type="submit"
                disabled={isSubmitting}
                className="flex items-center justify-center w-full gap-2 px-6 py-4 font-medium text-white bg-gradient-to-r from-green-500 to-blue-500 rounded-xl disabled:opacity-50"
              >
                {isSubmitting ? (
                  t.contact.form.sending
                ) : (
                  <>
                    <Send className="w-5 h-5" />
                    {t.contact.form.submit}
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
