import React, { useState, FormEvent, ChangeEvent } from 'react';
import { personalInfo, translations } from '../data/mock';
import { Mail, Phone, MapPin, Send, MessageSquare } from 'lucide-react';

interface ContactProps {
  lang: 'en' | 'id';
}

interface FormData {
  name: string;
  email: string;
  subject: string;
  message: string;
}

const initialForm: FormData = {
  name: '',
  email: '',
  subject: '',
  message: ''
};

const Contact: React.FC<ContactProps> = ({ lang }) => {
  const t = translations[lang];

  const [formData, setFormData] = useState<FormData>(initialForm);
  const [errors, setErrors] = useState<Partial<FormData>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  /* ================= VALIDATION ================= */
  const validateForm = (): boolean => {
    const newErrors: Partial<FormData> = {};

    if (!formData.name.trim()) newErrors.name = t.contact.validation.required;
    if (!formData.email.trim()) {
      newErrors.email = t.contact.validation.required;
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = t.contact.validation.invalidEmail;
    }
    if (!formData.subject.trim()) newErrors.subject = t.contact.validation.required;
    if (!formData.message.trim()) newErrors.message = t.contact.validation.required;

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  /* ================= HANDLERS ================= */
  const handleChange = (
    e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name as keyof FormData]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }
  };

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsSubmitting(true);

    try {
      // ⛳ Backend ready (example)
      // await fetch('/api/contact', {
      //   method: 'POST',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify(formData),
      // });

      await new Promise(res => setTimeout(res, 1500));

      setShowSuccess(true);
      setFormData(initialForm);

      setTimeout(() => setShowSuccess(false), 5000);
    } catch (err) {
      console.error(err);
    } finally {
      setIsSubmitting(false);
    }
  };

  const openWhatsApp = () => {
    const text =
      lang === 'en'
        ? 'Hello! I would like to discuss a project with you.'
        : 'Halo! Saya ingin mendiskusikan proyek dengan Anda.';
    window.open(
      `https://wa.me/${personalInfo.whatsapp}?text=${encodeURIComponent(text)}`,
      '_blank'
    );
  };

  /* ================= UI ================= */
  return (
    <section id="contact" className="px-6 py-20 bg-gray-800">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="mb-16 text-center">
          <h2 className="mb-4 text-4xl font-bold text-white md:text-5xl">
            {t.contact.title}
          </h2>
          <p className="max-w-2xl mx-auto text-gray-400">
            {t.contact.subtitle}
          </p>
        </div>

        <div className="grid grid-cols-1 gap-8 lg:grid-cols-3">
          {/* ===== LEFT INFO ===== */}
          <div className="space-y-6">
            {[
              {
                icon: Mail,
                title: t.contact.info.email,
                value: personalInfo.email,
                link: `mailto:${personalInfo.email}`
              },
              {
                icon: Phone,
                title: t.contact.info.phone,
                value: personalInfo.phone
              },
              {
                icon: MapPin,
                title: t.contact.info.location,
                value: personalInfo.location[lang]
              }
            ].map((item, i) => (
              <div
                key={i}
                className="p-6 transition bg-gray-900 border border-gray-700 rounded-2xl hover:border-green-500/50"
              >
                <div className="flex items-center justify-center w-12 h-12 mb-4 rounded-xl bg-green-500/20">
                  <item.icon className="text-green-400" />
                </div>
                <h3 className="mb-1 font-semibold text-white">
                  {item.title}
                </h3>
                {item.link ? (
                  <a
                    href={item.link}
                    className="text-sm text-gray-400 break-all hover:text-green-400"
                  >
                    {item.value}
                  </a>
                ) : (
                  <p className="text-sm text-gray-400">{item.value}</p>
                )}
              </div>
            ))}

            <button
              onClick={openWhatsApp}
              className="flex items-center justify-center w-full gap-2 py-4 font-medium text-white transition bg-green-500 shadow-lg rounded-xl hover:bg-green-600 hover:scale-105"
            >
              <MessageSquare size={20} />
              WhatsApp
            </button>
          </div>

          {/* ===== FORM ===== */}
          <div className="lg:col-span-2">
            <form
              onSubmit={handleSubmit}
              className="p-8 bg-gray-900 border border-gray-700 rounded-2xl"
              noValidate
            >
              {showSuccess && (
                <div className="p-4 mb-6 text-sm text-green-400 border rounded-xl bg-green-500/20 border-green-500/40">
                  {t.contact.success}
                </div>
              )}

              <div className="grid gap-6 mb-6 md:grid-cols-2">
                {['name', 'email'].map(field => (
                  <div key={field}>
                    <label className="block mb-2 text-sm font-medium text-white">
                      {t.contact.form[field as 'name' | 'email']}
                    </label>
                    <input
                      type={field === 'email' ? 'email' : 'text'}
                      name={field}
                      value={formData[field as keyof FormData]}
                      onChange={handleChange}
                      autoComplete={field}
                      aria-invalid={!!errors[field as keyof FormData]}
                      className={`w-full px-4 py-3 rounded-xl bg-gray-800 text-white border ${
                        errors[field as keyof FormData]
                          ? 'border-red-500'
                          : 'border-gray-700'
                      } focus:ring-2 focus:ring-green-500 outline-none`}
                    />
                    {errors[field as keyof FormData] && (
                      <p className="mt-1 text-xs text-red-400">
                        {errors[field as keyof FormData]}
                      </p>
                    )}
                  </div>
                ))}
              </div>

              {['subject', 'message'].map(field => (
                <div key={field} className="mb-6">
                  <label className="block mb-2 text-sm font-medium text-white">
                    {t.contact.form[field as 'subject' | 'message']}
                  </label>
                  {field === 'message' ? (
                    <textarea
                      rows={6}
                      name={field}
                      value={formData.message}
                      onChange={handleChange}
                      className={`w-full px-4 py-3 rounded-xl bg-gray-800 text-white border ${
                        errors.message ? 'border-red-500' : 'border-gray-700'
                      } focus:ring-2 focus:ring-green-500 outline-none resize-none`}
                    />
                  ) : (
                    <input
                      type="text"
                      name={field}
                      value={formData.subject}
                      onChange={handleChange}
                      className={`w-full px-4 py-3 rounded-xl bg-gray-800 text-white border ${
                        errors.subject ? 'border-red-500' : 'border-gray-700'
                      } focus:ring-2 focus:ring-green-500 outline-none`}
                    />
                  )}
                  {errors[field as keyof FormData] && (
                    <p className="mt-1 text-xs text-red-400">
                      {errors[field as keyof FormData]}
                    </p>
                  )}
                </div>
              ))}

              <button
                type="submit"
                disabled={isSubmitting}
                className="flex items-center justify-center w-full gap-2 py-4 font-medium text-white transition bg-green-500 rounded-xl hover:bg-green-600 disabled:bg-gray-700"
              >
                {isSubmitting ? (
                  <>
                    <span className="w-5 h-5 border-2 rounded-full border-white/30 border-t-white animate-spin" />
                    {t.contact.form.sending}
                  </>
                ) : (
                  <>
                    <Send size={18} />
                    {t.contact.form.send}
                  </>
                )}
              </button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Contact;
